import React from "react";
import Text from "./Text";

export default function BidItem({ data }) {
  const styles = {
    container: {
      display: "flex",
      alignItems: "center",
      borderRadius: "8px",
      backgroundColor: "#0F64A4",
      padding: "10px",
      marginBottom: "1em",
    },
    index: {
      width: "20px",
      textAlign: "center",
    },
    text: {
      fontSize: 21,
      fontColor: "#FFF",
    },
  };

  const { index, name, price } = data;
  return (
    <div style={styles.container}>
      <div style={styles.index}>
        <Text fontColor="#FFF">{`${index}`}</Text>
      </div>
      <div style={{ flex: 1 }}>
        <Text fontColor="#FFF">{` ${name}`}</Text>
      </div>
      <div>
        <Text fontColor="#FFF">{`$${parseFloat(price).toFixed(2)}`}</Text>
      </div>
    </div>
  );
}
