import React from "react";
import BidItem from "./BidItem";
import HorizontalBar from "./HorizontalBar";
import Panel from "./Panel";
import Text from "./Text";

export default function FlightDetails({ data }) {
  const styles = {};

  const { departure, arrival, from, to, type, flight } = {
    departure: "12:30 AM",
    arrival: "2:20 AM",
    from: "DCA",
    to: "DFW",
    type: "Boeing 747-800",
    flight: "AA 2010",
  };
  const bids = [{ name: "John Doe", price: "300" }];
  return (
    <Panel style={{ marginLeft: "1em", flex: 3 }}>
      <div style={{ display: "flex", alignItems: "flex-end" }}>
        <Text>Bid Details</Text>
      </div>
      <HorizontalBar height={2} />
      <div style={{ display: "flex", flexDirection: "column", padding: "2em" }}>
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            flex: 1,
            justifyContent: "space-between",
          }}
        >
          <div style={{ flex: 1, textAlign: "end" }}>
            <Text bold>{`${from}`}</Text>
          </div>
          <div style={{ flex: 1, textAlign: "center" }}>
            <Text bold>{`-->`}</Text>
          </div>
          <div style={{ flex: 1 }}>
            <Text bold>{`${to}`}</Text>
          </div>
        </div>
        <div>
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              flex: 1,
              justifyContent: "space-between",
            }}
          >
            <Text>Depart At</Text>
            <Text>Arrive At</Text>
          </div>
          <HorizontalBar />
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              flex: 1,
              justifyContent: "space-between",
            }}
          >
            <Text bold>{`${departure}`}</Text>
            <Text bold>{`${arrival}`}</Text>
          </div>
        </div>
        <div>
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              flex: 1,
              justifyContent: "space-between",
            }}
          >
            <Text>Aircraft Type</Text>
            <Text>Flight No.</Text>
          </div>
          <HorizontalBar />
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              flex: 1,
              justifyContent: "space-between",
            }}
          >
            <Text bold>{`${type}`}</Text>
            <Text bold>{`${flight}`}</Text>
          </div>
        </div>
        <div>
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              flex: 1,
              justifyContent: "space-between",
            }}
          >
            <Text>Bid History</Text>
            <Text>Pilot | Crew</Text>
          </div>
          {[...bids, ...bids, ...bids].map((bid, index) => (
            <BidItem data={{ ...bid, index }} />
          ))}
        </div>
      </div>
    </Panel>
  );
}
